mapper.select:
select t.id, t.account, t.pass_word,
t.is_lock, t.password_prompt,t.create_date from t_admin t where
t.account=#{account} and t.pass_word=#{passWord}

select t.id, t.account, t.pass_word, t.is_lock, t.password_prompt,t.create_date from t_admin t
and t.account like concat(concat('%',#{account}),'%')
ORDER BY t.create_date desc limit #{startRow},#{pageSize}
